import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../App.css"; // Adjusted path based on new folder structure

const ChooseDocumentPage = () => {
  const navigate = useNavigate();
  const [selectedDocument, setSelectedDocument] = useState(null);
  const documents = Array.from({ length: 6 }, (_, i) => `Document ${i + 1}`);

  const handleDocumentSelect = (doc) => {
    setSelectedDocument(doc);
  };

  const handleNext = () => {
    if (selectedDocument) {
      navigate("/edit-document");
    } else {
      alert("Please select a document or upload a new one.");
    }
  };

  return (
    <div
      style={{
        backgroundColor: "#F0DFC3",
        fontFamily: "Proxima Nova",
        minHeight: "100vh",
        position: "relative",
        padding: "50px 100px",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "50px",
          fontWeight: "bold",
          color: "#A6785E",
          textAlign: "center",
          marginBottom: "50px",
        }}
      >
        Choose Your Document
      </h1>

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "flex-start",
        }}
      >
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "20px",
            fontSize: "30px",
            color: "#50464E",
          }}
        >
          {documents.map((doc, index) => (
            <label key={index} style={{ cursor: "pointer" }}>
              <input
                type="checkbox"
                checked={selectedDocument === doc}
                onChange={() => handleDocumentSelect(doc)}
                style={{ marginRight: "10px" }}
              />
              {doc}
            </label>
          ))}
        </div>

        <div
          style={{
            border: "2px dashed #50464E",
            borderRadius: "10px",
            width: "400px",
            height: "300px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            color: "#50464E",
            fontSize: "30px",
            cursor: "pointer",
          }}
        >
          Upload Document
        </div>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginTop: "50px",
        }}
      >
        <button
          style={{
            fontSize: "30px",
            backgroundColor: "#DEA93D",
            color: "#50464E",
            padding: "10px 30px",
            borderRadius: "10px",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
          }}
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default ChooseDocumentPage;
